var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },

	isDatePast: function(strDate){
		// Create GlideDateTime objects for the current date and the passed in date
		var rightnow = new GlideDateTime();
		var testdate = new GlideDateTime(strDate);

		// If the testdate is before rightnow, return true, else return false
		if (testdate.before(rightnow)) {
			return true;
		}
		else {
			return false;
		}
	},

	isDateToday: function(strDate){

		var dateHoySistemaA = new GlideDateTime(strDate);		
		gs.info('dateHoySistemaA.getLocalDate()' + dateHoySistemaA.getLocalDate());
		//gs.info('A strDate: ' + strDate);
		// Create GlideDateTime objects for the current date and the passed in date		
		var dateHoySistema = new GlideDate();		
		var dateParts = strDate.split(" "); // Dividir el string en partes usando el espacio como separador
		var dateOnly = dateParts[0];

// 		gs.info('dateParts: ' + dateParts[0]);
// 		gs.info('dateOnlytype: ' + typeof dateOnly);
// 		gs.info('dateOnly: ' + dateOnly);
		var dateFormulario = new GlideDate();		
		dateFormulario.setValue(dateOnly);		
		//gs.info('dateHoySistema.getValue() == dateFormulario.getValue()' + dateHoySistema.getValue() + '==' + dateFormulario.getValue());		
		if(dateHoySistema.getValue() == dateFormulario.getValue()) {
			//gs.info('validaciÃ³n si: ' + true);
			return true;
		}
		else {
			return false;
		}
	},

    type: 'NeedItUtils'
};
